import { View, Text, FlatList, StyleSheet, Image } from 'react-native'
import React, { useLayoutEffect } from 'react';
import { MEALS } from '../data/dummydata';

const MealDetailScreen = ({route, navigation}) => {
    const ingredients = route.params.ingredients
    console.log("ingredients", ingredients)
    const steps = route.params.steps
    const ImageSource = route.params.imageUrl 
    console.log("Image is", ImageSource)
    console.log(typeof(ImageSource))
    const title = route.params.title

    // const MealTitle = MEALS.find((Meal)=>Meal.) 
    useLayoutEffect(()=> {
        navigation.setOptions({
            title : title
        })
    })

    const MealIngredients = (itemData) => {
        console.log("ingredientsData", itemData.item)
        return(
                 <View style={styles.ingredientsBox}>
                     <Text style={{padding : 5}}>*</Text>
                     <Text style={styles.ingredients}>{itemData.item}</Text>
                 </View>
        )
    }

    
    const MealSteps = (itemData) => {
        console.log("Steps", itemData.item)
        return(
                 <View style={styles.ingredientsBox}>
                     <Text style={{padding : 5}}>*</Text>
                     <Text style={styles.ingredients}>{itemData.item}</Text>
                 </View>
        )
    }
  return (
    <>
    <View style = {{flex : 1}}>
      <Image style={styles.imageStyle} source={{uri : ImageSource}} />
      {/* <Image
      source={{uri: 'https://example.com/image.png'}}
      style={{width: 200, height: 200}}
    /> */}
      <Text style={styles.title} >{title}</Text>
    </View>
    
    <View style={styles.container}>
      <Text style={{fontWeight:"bold" , padding : 8,   fontSize : 20, color : "red"}}>ingredients</Text>
      <FlatList data={ingredients} keyExtractor={(item)=>item} renderItem={MealIngredients} />
    </View>
    <View style={styles.container}>
    <Text style={{fontWeight:"bold" , padding : 8,   fontSize : 20, color:"red"}}>Steps</Text>
      <FlatList data={steps} keyExtractor={(item)=>item} renderItem={MealSteps} />
    </View>
    </>
  )
}

export default MealDetailScreen


const styles = StyleSheet.create({
    imageStyle : {
        width : "100%",
        height : 200
    },
    container : {
        // flex :1,
        backgroundColor : "white",
        margin  : 15,
        height : 200,
        borderWidth : 1,
        borderRadius : 8
    },
    ingredientsBox:{
        flexDirection : "row",
    },
    ingredients : {
        fontSize : 15,
        fontWeight : "bold",
        marginHorizontal:5
    },
    title : {
        textAlign : "center",
        color : "white",
        fontSize : 15,
        margin : 8
    }
})
