import { View, Text, FlatList , StyleSheet} from 'react-native'
import React from 'react'
import { CATEGORIES } from '../data/dummydata'
import CategoryGridTile from '../component/CategoryGridTile';
console.log(CATEGORIES)

const DATA = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    title: 'First Item',
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    title: 'Second Item',
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    title: 'Third Item',
  },
];
console.log(DATA)

const CategoriesScreen = ({navigation}) => {

  const renderCategoryItem = (itemData) => {
    const PressHandler = () => {
       navigation.navigate("MealsOveriew")
    }
      return <CategoryGridTile title={itemData.item.id} color={itemData.item.color} onPress={PressHandler} /> ;
  }
  
  return (<View style={styles.container}>
    <FlatList  data={CATEGORIES} keyExtractor={(item)=> item.id} renderItem={renderCategoryItem} 
         numColumns={2} />
    </View>
    
  )
}

export default CategoriesScreen;


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#000',
    //   alignItems: 'center',
    //   justifyContent: 'center'
    },
  });
