import { View, Text , TextInput, StyleSheet, Button, ImageBackground} from 'react-native'
import React, { useState } from 'react'

export default function WelcomeScreen() {
    const [user, setUser] = useState("")
    const handleInput = (text) => {
        setUser(text)
    }
  return (
    <View style={styles.container}>
       <ImageBackground
        source={{
          uri: 
'https://media.geeksforgeeks.org/wp-content/uploads/20220217151648/download3.png',
        }}
        resizeMode="stretch"
        style={styles.img}>
        <TextInput placeholder="Geeks for Geeks" style={styles.input} />
      </ImageBackground>
      <View style={styles.innerContainer}>
      {/* <Text style={styles.Name}>Name</Text> */}
      <TextInput style={styles.input} value={user} onChangeText={handleInput} placeholder="Enter your Name" />
      <Button  title='Submit' />
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
    container : {
        flex : 1,
        justifyContent : "center"
    },
    innerContainer : {
        marginHorizontal : 10,
        padding : 10,
        alignItems : "center"
    },
    // Name : {
    //     fontSize : 15,
    //     color : "orange",
    //     fontWeight : "bold",
    // },
    input : {
        borderWidth : 2,
        borderBottomColor : "white", 
        width : "70%",
        marginHorizontal : 8,
        height : 50,
        color : "white",
        marginBottom : 15
    }
})
